mapper.select:
select id, name, company, company_id, department, type, province, city, district,address,
industry, function, is_remote, description, requirement, salary, welfare,duty_per_week,
duration, user_id, receive_method, receive_email, status, create_time,update_time
from cn_job


    and name LIKE "%"#{name, jdbcType=VARCHAR}"%"


    and type = #{type, jdbcType=VARCHAR}


    and status = #{status, jdbcType=TINYINT}


    and name like concat(concat("%",#{searchValue}),"%") or company like concat(concat("%",#{searchValue}),"%") or department like concat(concat("%",#{searchValue}),"%");


  order by create_time desc
  limit #{iDisplayStart,jdbcType=INTEGER},#{iDisplayLength,jdbcType=INTEGER}

SELECT submit.id as submit_id,submit.job_id, job.user_id, user.name as user_name FROM 	cn_job_submit submit
JOIN cn_job job ON submit.job_id=job.id
JOIN cn_user user ON submit.user_id=user.id where submit.job_id= #{jobId};
