mapper.select:
SELECT id,evaluation
FROM cn_resume
WHERE user_id = #{userId}

mapper.update:
update cn_resume set
evaluation = #{evaluation},
update_time = #{updateTime}
where user_id = #{userId}

mapper.select:
SELECT submit.id as submit_id,submit.job_id,submit.user_id,job.name as job_name,job.company,job.company_id,company.logo,submit.status
FROM cn_job_submit submit JOIN cn_job job ON submit.job_id=job.id JOIN cn_company company on job.company_id=company.id
WHERE submit.user_id= #{userId};
